module instruction_box(
    input [9:0] x, y,
    output logic [11:0] rgb
);

    // Parameters for screen dimensions and color
    parameter WIDTH = 511;
    parameter HEIGHT = 95;
    parameter COLOR_ON = 12'hFFF;   // White for "on" pixels
    parameter COLOR_OFF = 12'h4AC; // Black for "off" pixels

    // ROM address based on the y-coordinate (row)
    logic [6:0] rom_addr;           // Supports up to 128 rows
    logic [510:0] out;         // ROM output for a row (511 bits)

    // Map (x, y) to ROM
    assign rom_addr = y[6:0];       // Use y as the ROM row address
    
always @*
    case(rom_addr)
        0: out = 511';
        1: out = 511';
        2: out = 511';
        3: out = 511';
        4: out = 511';
        5: out = 511';
        6: out = 511';
