        18: out = 511';
        19: out = 511';
        20: out = 511';
        21: out = 511';
        22: out = 511';
        23: out = 511';
        24: out = 511';
        25: out = 511';
        26: out = 511';
        27: out = 511';
        28: out = 511';
        29: out = 511';
        30: out = 511';
        31: out = 511';
