        90: out = 511';
        91: out = 511';
        92: out = 511';
        93: out = 511';
        94: out = 511';
    endcase    
    // Determine the pixel color based on x-coordinate
    always @* begin
        if (x < WIDTH) begin
            rgb = out[WIDTH - x] ? COLOR_ON : COLOR_OFF;
        end else begin
            rgb = COLOR_OFF; // Out of bounds defaults to off
        end
    end
endmodule    
